mapper.select:
		SELECT b_sub.b_no , b_title, b_date, b_write, b_image, b_place, member_no, b_choose, b_share
		FROM (
  		SELECT ROW_NUMBER() OVER (ORDER BY b_date DESC) AS b_no, b_no AS num, b_title, b_date, b_write, b_image, b_place, member_no, b_choose, b_share
  		FROM business where b_share =1
		) AS b_sub
		WHERE b_sub.b_no  BETWEEN #{start} and #{end};

select *
from (
select ROW_NUMBER() over(order by b_date desc) as b_no1, b.*
from business b) business2
where business2.b_no between #{start} and #{end}
